mapper.select:
SELECT employee0_.employee_name AS employee_name, employee0_.email_address AS email_address, employee0_.age AS age, employee0_.gender AS gender, employee0_.phone_number AS phone_number, employee0_.id_card_no AS id_card_no, employee0_.bank_card AS bank_card, employee0_.entry_time AS entry_time, employee0_.position_id AS position_id, employee0_.employee_status AS employee_status, employee0_.employee_type AS employee_type, employee0_.employee_dynamic AS employee_dynamic, employee0_.id AS id, employee0_.resignation_time AS resignation_time, employee0_.remaining_annual_leave_day AS remaining_annual_leave_day, employee0_.remaining_lieu_leave_day AS remaining_lieu_leave_day, employee0_.social_security_fund_ratio AS social_security_fund_ratio, position0_.position_name AS position_name, department0_.id AS department_id, department0_.department_name AS department_name FROM employee employee0_ JOIN position position0_ ON employee0_.position_id = position0_.id JOIN department department0_ ON position0_.department_id = department0_.id AND employee0_.employee_status != 'INVALID_STATUS'
employee0_.employee_name=#{employeeName,jdbcType=VARCHAR}
employee0_.employee_type=#{employeeType,jdbcType=VARCHAR}
